+.container {
+  flex: 1;
+  padding: 20px;
+  position: relative;
+  top: 100px;
+}
+
+.treeContainer {
+  display: flex;
+  justify-content: center;
+  align-items: center;
+}
+
+.node {
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+  padding: 10px;
+  border: 1px solid #ccc;
+  border-radius: 5px;
+  margin: 10px;
+  position: relative;
+  transition: box-shadow 0.3s ease, transform 0.3s ease;
+  cursor: pointer;
+
+  &:hover {
+    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.1);
+    transform: translateY(-5px);
+  }
+}
+
+.node .icon {
+  font-size: 24px;
+  margin-bottom: 5px;
+}
+
+.node .name {
+  font-weight: bold;
+}
+
+.node .email {
+  font-size: 12px;
+  color: #666;
+}
+
+.node .id {
+  font-size: 12px;
+  color: #888;
+}
+
+.lineWrapper {
+  position: absolute;
+  top: 100%;
+  left: 50%;
+  width: 50%;
+  height: 30px;
+}
+
+.line {
+  position: absolute;
+  border-left: 2px solid #ccc;
+  height: 100%;
+  width: 100%;
+}
+
+.lineLeft {
+  left: -50%;
+}
+
+.lineRight {
+  right: -50%;
+}
+
+.children {
+  display: flex;
+  justify-content: center;
+  margin-top: 20px;
+}
+
+.child {
+  margin: 0 10px;
+}
+
+.addChild {
+  margin-top: 10px;
+}
+
+.addChildButton {
+  padding: 5px 10px;
+  font-size: 12px;
+}
+
+.buttonWrapper {
+  display: flex;
+  justify-content: center;
+  margin-bottom: 20px;
+}
+
+.toggleButton {
+  padding: 10px 20px;
+  font-size: 16px;
+}
+
+.modal {
+  position: fixed;
+  top: 0;
+  left: 0;
+  right: 0;
+  bottom: 0;
+  background: rgba(0, 0, 0, 0.5);
+  display: flex;
+  justify-content: center;
+  align-items: center;
+}
+
+.modalContent {
+  background: #fff;
+  padding: 20px;
+  border-radius: 5px;
+  position: relative;
+}
+
+.close {
+  position: absolute;
+  top: 10px;
+  right: 10px;
+  font-size: 24px;
+  cursor: pointer;
+}
+
+.form {
+  display: flex;
+  flex-direction: column;
+}
+
+.form label {
+  margin-bottom: 10px;
+}
+
+.form input {
+  padding: 8px;
+  margin-top: 5px;
+}
+
+.error {
+  color: red;
+  margin-top: 10px;
+}
+
+.success {
+  color: green;
+  margin-top: 10px;
+}
+
+.sendCoins {
+  position: absolute;
+  bottom: -35px; /* Adjust to position the button relative to the node */
+}
+
+.sendCoinsButton {
+  padding: 5px 10px;
+  font-size: 12px;
+  // margin-left: 50px;
+  align-items: center;
+  margin-bottom: 5px;
+  transition: background-color 0.3s ease, box-shadow 0.3s ease;
+  cursor: pointer;
+
+  &:hover {
+    background-color: #007bff;
+    color: white;
+    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.3);
+  }
+
+  &.updating {
+    animation: pulse 1s infinite;
+  }
+}
+
+.coinsPopup {
+  display: flex;
+  flex-direction: column;
+  align-items: center;
+}
+
+.coinsPopup input {
+  padding: 8px;
+  margin-bottom: 5px;
+}
+
+.updateCoinsButton {
+  padding: 5px 10px;
+  font-size: 12px;
+}
+
+@keyframes pulse {
+  0% {
+    transform: scale(1);
+  }
+  50% {
+    transform: scale(1.1);
+  }
+  100% {
+    transform: scale(1);
+  }
+}

